import { makeStyles, Table, TableCell, TableHead, TablePagination, TableRow, TableSortLabel } from '@material-ui/core'
import React, { useState } from 'react'
import { insertEmployee } from '../../services/employeeService'

const useStyles = makeStyles(theme => ({
    table: {
        marginTop: theme.spacing(3),
        '& thead th': {
            fontWeight: '600',
            color: theme.palette.primary.main,
            backgroundColor: theme.palette.primary.light,
        },
        '& tbody td': {
            fontWeight: '300',
        },
        '& tbody tr:hover': {
            backgroundColor: '#fffbf2',
            cursor: 'pointer',
        },
    },
}))

export default function useTable(records,headCells) {

    const classes = useStyles();

    const pages =[5, 10, 25]
    const [page, setPage] = useState(0)
    const [rowsPerPage, setRowsPerPage]=useState(pages[page])
    const [order,setOrder]=useState()
    const [orderBy,setOrderBy] = useState()

    const TblContainer = props => (
        <Table className={classes.table}>
            {props.children}
        </Table>
    )

    const TblHead = props => {

        const handleSortRequest = cellId => {
            const IsAsc = orderBy === cellId && order === "asc";
            setOrder(isAsc)
            setOrderBy(cellId)
        }

        return (<TableHead>
            <TableRow>
                {headCells.map(headCell => (<TableCell key={headCell.id}>
                    <TableSortLabel
                    onClick = { () => {handleSortRequest(headCell.id)}}>
                        {headCell.label}
                    </TableSortLabel>
                </TableCell>))}
            </TableRow>
        </TableHead>)
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage)
    }

    const handleChangeRowsPerPage = event => {
        setRowsPerPage(parseInt(event.target.value,10))
        setPage(0)
    }

    const TblPagination = () => (<TablePagination
        component="div" 
        page={page}
        rowsPerPageOptions={pages}
        rowsPerPage={rowsPerPage}
        count={records.length}
        onChangePage={handleChangePage}
        onChangeRowsPerPage = {handleChangeRowsPerPage}
    />)

    const recordsAfterPagingAndSorting = () => {
        return records.slice(page*rowsPerPage,(page+1)*rowsPerPage)
    }

    return {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    }
}
