import { Grid, makeStyles, TextField } from '@material-ui/core';
import React, { useState, useEffect } from 'react'
import { useForm, Form } from '../../components/useForm';

const initialFValues = {
    id :0,
    fullName:'',
    email:'',
    mobile:'',
    city:'',
    gender:'male',
    departmendId:'',
    hireDate:new Date(),
    isPermanent: false,
}

export default function EmployeesForm(){

    const {
        values,
        setValues,
        handleInputChange
    } = useForm(initialFValues);

    return (
        <Form>
            <Grid container>
                <Grid item xs={6}>
                   <TextField 
                        variant="outlined"
                        label="Full Name"
                        name="fullName"
                        value={values.fullName}
                        onChange = {handleInputChange}
                   />
                   <TextField 
                        variant="outlined"
                        label="Email"
                        name="email"
                        value={values.email}
                        onChange= {handleInputChange}
                   />
                </Grid>
                <Grid item xs={6}>

                </Grid>
            </Grid>
        </Form>
    )
}
